import './card.styles.css';
import React from 'react';

const Card = ({monster: { email, name, id}}) => (
    <div className='card-container' key={id}>
        <img alt={`monster ${name}`} src={`https://robohash.org/${id}?set=set1&size=180x180`}/>
        <h2>{name}</h2>
        <p>{email}</p>
    </div>
);

export default Card;
