import { Component } from 'react';

import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor() {
    // console.log('constructor');
    super();

    // initial state
    this.state = {
      monsters: [],
      searchField: ''
    };
  };

  componentDidMount() {
    // console.log('componentDidMount');
    fetch('https://jsonplaceholder.typicode.com/users')
    .then(response => response.json()) // uses what was returned from the previous line of code and parses the text body into a .json format
    // .then(users => console.log(users)) // after the text was parse, it can then console.log it
    .then(users => {
      this.setState(
        () => {
          return {monsters: users};
        }, 
        () => {
          // console.log(this.state);
        }
      )
    })
  };

  onSearchChange = event => {
    // console.log(event); // The event just return information regarding what changed

    let searchField = event.target.value.toLocaleLowerCase();
    this.setState(() => {
      return { searchField }; // same as saying return { searchField: searchField };
    });
  }

  render() {
    // console.log('render');

    let monsters = this.state.monsters;
    let filteredMonsters = monsters.filter(monster => {return monster.name.toLocaleLowerCase().includes(this.state.searchField)});

    return (
      <div className="App">

        {/* When there's a change in the search field, it gets processed here 
            This happens only when the input element is changed in some way & ONLY impacts this element 
            Returns a new searchField value in the state */}
        <input className='search-box' type='search' placeholder='search monsters' onChange={this.onSearchChange}/>

        {
        /* Where the filtered monsters actually get rendered onto the page */
          filteredMonsters.map(monster => {
            return (
              <div key={monster.id}>
                <h1>{monster.name}</h1>
              </div>
            );
          })
        }
      </div>
    );
  };

}

export default App;
