import { Component } from 'react';

import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor() {
    super();

    this.state = {
      name: 'Cristiana',
      church: 'Passion for Christ'
    }
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>Hi {this.state.name}! You go to {this.state.church}</p>
          <button onClick={() => {
            this.setState({name: 'Naomi'});
          }}>Change Name</button>
        </header>
      </div>
    );
  };

}

export default App;
