import { Component } from 'react';

import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor() {
    super();

    this.state = {
      monster1: {
        name: 'Linda'
      },
      monster2: {
        name: 'Frank'
      },
      monster3: {
        name: 'Jacky'
      }
    }
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Hi {this.state.name.firstName} {this.state.name.lastName}! You go to {this.state.church} Church!
          </p>
          <button
            onClick = {() => {
              this.setState(() => {
                return {
                  name: {firstName: 'Naomi', lastName: 'Mateas'}
                }
              }, () => {
                console.log(this.state);
              });
            }}
          >
            Change Name
          </button>
        </header>
      </div>
    );
  };

}

export default App;
